mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE CI_IDX = #{ciIdx}
  AND U_IDX = #{uIdx}

		 SELECT * FROM
		( SELECT
		ROWNUM RN, A.*  FROM
		( SELECT B.*, U.U_NICKNAME 
			FROM BOARD B, USERS U WHERE CI_IDX = #{sort} 
			AND B.U_IDX = U.U_IDX  
        ORDER BY B_IDX DESC) A
		) WHERE RN BETWEEN #{start} AND #{end}

SELECT B.*, U.U_NICKNAME 
  FROM BOARD B, USERS U
 WHERE U_IDX = #{uIdx}
   AND CI_IDX = #{ciIdx}

mapper.update:
UPDATE CAMPAIGN
   SET CI_ESTIMATEDPOINT = CI_ESTIMATEDPOINT + 10
 WHERE CI_IDX = #{ciIdx}
