ORDER BY K_CONTENT) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM
( SELECT
ROWNUM RN, A.* FROM
( SELECT * FROM BOARD ORDER BY B_IDX DESC) A
) WHERE
RN
BETWEEN #{start}
AND #{end}

